risk2_countries = ["Argentina", "Australia", "Austria", "Bangladesh", "Brazil", "Belgium", "Cambodia", "Canada", "Chile", "Croatia", "Cuba", "Cyprus", "Czech Republic", "Denmark", "Estonia", "Fiji", "Finland", "France", "Germany", "Ghana", "Greece", "Iceland", "India", "Indonesia", "Iran", "Ireland", "Israel", "Italy", "Japan", "Jordan", "Korea, Democratic People's Republic of", "Korea, Republic of", "Kuwait", "Latvia", "Lebanon", "Liechtenstein", "Malaysia", "Maldives", "Mauritius", "Mexico", "Nepal", "Netherlands", "Norway", "Pakistan", "Philippines", "Portugal", "Romania", "Russia", "Saudi Arabia", "Senegal", "Sierra Leone", "Singapore", "Spain", "Sri Lanka", "Sweden", "Switzerland", "Tanzania", "Thailand", "Tunisia", "Turkey", "Uganda", "United Arab Emirates", "United Kingdom"]
risk3_countries = ["Macao", "China", "Hong Kong", "Taiwan"]
risklevel = 0 #0 is no risk, 1 is medium, 2 is high, 3 is exception

def sortrisk(country):
    if country in risk2_countries:
        risklevel = 2
    elif country in risk3_countries:
        risklevel = 3
    else:
        risklevel = 1
    return risklevel

from flask import Flask, redirect, url_for, render_template, request

app = Flask (__name__)
@app.route("/", methods=["GET","POST"])
def home():
	if request.method=="POST":
		country = request.form["country"]
		print (country)
		if sortrisk(country) == 1:
			return redirect(url_for("risk1"))
		elif sortrisk(country) == 2:
			return redirect(url_for("risk2"))
		elif sortrisk(country) == 3:
			return redirect(url_for("risk3"))
		else:
			return render_template("home.html")
	else:	
		return render_template("home.html")

@app.route("/risk1")
def risk1():
	return render_template("risk1.html")

@app.route("/risk2")
def risk2():
	return render_template("risk2.html")

@app.route("/risk3")
def risk3():
	return render_template("risk3.html")

@app.route("/hotels")
def hotels():
	return render_template("hotels.html", hotel=hotels)


#start website
if __name__ == "__main__":
	app.run(debug=True,host="localhost")